import React from 'react'

const Minusbox = (props) => {
  // minusbox的開關
  const minusbox_disappear = props.minusbox_switch ? "" : "disappear"
  
  // 傳資料到後端
  const [plusbox_name, set_plusbox_name] = useState("")
  const [plusbox_price, set_plusbox_price] = useState("")
  const [plusbox_today_date, set_plusbox_today_date] = useState("")
  const [plusbox_note, set_plusbox_note] = useState("")

  const [plusbox_data, set_plusbox_data] = useState({})

  useEffect(() => {
    set_plusbox_data({
      name: plusbox_name,
      shop: "",
      price: plusbox_price,
      today_date: plusbox_today_date,
      note: plusbox_note,
    })

  }, [plusbox_name, plusbox_price, plusbox_price, plusbox_note])


  const post_plusbox_data_function = () => {

    const post_plusbox_data = axios.post("http://localhost:8080/postData", plusbox_data)
    props.set_plusbox_switch(false)
  }

  return (
    <div className={`minusbox ${minusbox_disappear}`}>
      <div className='d-flex flex-column p-3'>
        <h3>新增支出</h3>
        <div>
          <h5>名稱:</h5>
          <input type="text" />
          <h5 className='mt-2'>店名:</h5>
          <input type="text" />
          <h5 className='mt-2'>價格:</h5>
          <input type="text" />
          <h5 className='mt-2'>日期:</h5>
          <input type="date" />
          <h5 className='mt-2'>備註:</h5>
          <input type="text" />
        </div>
        <div className='d-flex justify-content-end mt-3'>
          <button className=''>確定</button>
          <button className='ms-3' onClick={() => { props.set_minusbox_switch(false) }}>取消</button>
        </div>

      </div>


    </div>
  )
}

export default Minusbox
