import React from 'react'
import { useEffect, useState } from 'react';

const Total = (props) => {
  // 篩選日期
  const [start_date, set_start_date] = useState("")
  const [end_date, set_end_date] = useState("")

  props.set_select_date_data(
    props.database_data.filter(item => {
      return item.today_date <= end_date && item.today_date >= start_date
    })
  )


  // 不同button被click後換顏色跟字體跟價錢
  const [button_state, set_button_state] = useState(1)
  const change_bg_color = button_state == 1 ? "bg_gray" : button_state == 2 ? "bg_green" : "bg_yellow"
  const change_text_color = button_state == 1 ? "text_gray" : button_state == 2 ? "text_green" : "text_yellow"
  const change_button_state1 = () => {
    set_button_state(1)
  }
  const change_button_state2 = () => {
    set_button_state(2)
  }
  const change_button_state3 = () => {
    set_button_state(3)
  }

  return <div className='mt-5'>
    <div className='row mx-5'>
      <div className={`col-3 p-3  border ${change_bg_color}`}>
        <div className='main_button_box'>
          <button onClick={() => { change_button_state1() }}></button>
          <button onClick={() => { change_button_state2() }}></button>
          <button onClick={() => { change_button_state3() }}></button>
        </div>
        <div className='d-flex flex-column justify-content-center align-items-center'>
          <h1 className='text-light'>總</h1>
          <h3 className='text-light'>收支</h3>
        </div>
      </div>
      <div className='col-9 row p-5 d-flex flex-column border'>
        <div className='border text-center'>
          <h1 className={`${change_text_color}`}>$2345</h1>
        </div>
        <div className='border d-flex justify-content-center text-center '>
          <h5>開始日期:</h5>
          <input type="date" value={start_date} onChange={(e) => { set_start_date(e.target.value) }} className='mx-5' />
          <h5>結束日期:</h5>
          <input type="date" value={end_date} onChange={(e) => { set_end_date(e.target.value) }} className='mx-5' />
        </div>

      </div>
    </div>



  </div>
}

export default Total;
