import './Home.css';
import { useEffect, useState } from 'react';
import Head from './component/head/head';
import Main from './component/main/main';
import Total from './component/total/total';
import "bootstrap/dist/css/bootstrap.css"
import axios from 'axios';




function App() {
  // 一開始就取得input資料
  const [database_data, set_database_data] = useState([])
  const [account_plus_data, set_account_plus_data] = useState([])
  const [account_minus_data, set_account_minus_data] = useState([])
  //篩選過收支的資料
  const [select_mode, set_select_mode] = useState([])
  // console.log(select_mode)
  // 篩選過日期的資料
  const [select_date_data, set_select_date_data] = useState([])
  // console.log(select_date_data)
  // 輸入的日期
  const [start_date, set_start_date] = useState("")
  const [end_date, set_end_date] = useState("")
  //篩選過的資料的價格計算
  const [price_total, set_price_total] = useState([])



  useEffect(() => {
    const get_data = axios.get("http://localhost:8080/getData")
      .then(response => {
        set_database_data(response.data)
      })
      .catch(error => {
        console.log(error)
      })

      const get_account_plus_data = axios.get("http://localhost:8080/getDataplus")
      .then(response => {
        set_account_plus_data(response.data)
      })
      .catch(error => {
        console.log(error)
      })

      const get_account_minus_data = axios.get("http://localhost:8080/getDataminus")
      .then(response => {
        set_account_minus_data(response.data)        
      })
      .catch(error => {
        console.log(error)
      })
      set_database_data(account_plus_data.concat(account_minus_data));
    }, [])

  // 更新後取得input資料

  const get_data_function = () => {
  //   const get_data = axios.get("http://localhost:8080/getData")
  //     .then(response => {
  //       set_database_data(response.data);
  //       set_select_mode(response.data)


  //     })
  //     .catch(error => {
  //       console.log(error)
  //     })
  }


  return (
    <div>
      <Head get_data_function={get_data_function} set_database_data={set_database_data} />
      <Total database_data={database_data} set_database_data={set_database_data} select_mode={select_mode} set_select_mode={set_select_mode} start_date={start_date} set_start_date={set_start_date} end_date={end_date} set_end_date={set_end_date} select_date_data={select_date_data} set_select_date_data={set_select_date_data} price_total={price_total} set_price_total={set_price_total} />
      {select_date_data.length === 0 ? (
        database_data.map(({ id, name, shop, price, today_date, note }) => (
          <Main
            key={id}
            id={id}
            name={name}
            shop={shop}
            price={price}
            today_date={today_date}
            note={note}
          />
        ))
      ) : (
        select_date_data.map(({ id, name, shop, price, today_date, note }) => (
          <Main
            key={id}
            id={id}
            name={name}
            shop={shop}
            price={price}
            today_date={today_date}
            note={note}
          />
        ))
      )}
    </div>
  );
}

export default App;
