import React from 'react'
import { useEffect, useState } from 'react';

const Total = (props) => {

  // 篩選日期

  useEffect(() => {
    const select_date = props.select_mode.filter(item => {
      const itemDate = new Date(item.today_date);
      const startDate = props.start_date !== "" ? new Date(props.start_date) : null;
      const endDate = props.end_date !== "" ? new Date(props.end_date) : null;

      return (!startDate || itemDate >= startDate) && (!endDate || itemDate <= endDate);
    });

    props.set_select_date_data(select_date);
  }, [props.start_date, props.end_date]);

  //價格計算
  //總收支
  const [sum_total, set_sum_total] = useState("");

  const total = () => {
    set_sum_total(sum_plus - sum_minus)
  }
  // 總收入
  const [sum_plus, set_sum_plus] = useState("");

  const total_plus = () => {
    const inside_sum_plus = props.select_date_data
      .filter(item => item.price.charAt(0) === "+")
      .map(item => parseInt(item.price.slice(1)));

    const sum_plus_total = inside_sum_plus.reduce((total, num) => total + num, 0);

    set_sum_plus(sum_plus_total);

    return sum_plus_total;
  };

  // 總支出
  const [sum_minus, set_sum_minus] = useState("");

  const total_minus = () => {
    const inside_sum_minus = props.select_date_data
      .filter(item => item.price.charAt(0) === "-")
      .map(item => parseInt(item.price.slice(1)));

    const sum_minus_total = inside_sum_minus.reduce((total, num) => total + num, 0);

    set_sum_minus(sum_minus_total);

    return sum_minus_total;
  };



  // 不同button被click後換顏色跟字體跟價錢和模式
  const [button_state, set_button_state] = useState(1)
  const change_bg_color = button_state == 1 ? "bg_gray" : button_state == 2 ? "bg_green" : "bg_yellow"
  const change_text_color = button_state == 1 ? "text_gray" : button_state == 2 ? "text_green" : "text_yellow"

  const change_button_state1 = () => {
    set_button_state(1)
    props.set_render_data(props.database_data)


  }

  const change_button_state2 = () => {
    set_button_state(2)

  }


  const change_button_state3 = () => {
    set_button_state(3)


  }


  return <div className='mt-5'>
    <div className='row mx-5'>
      <div className={`col-3 p-3  border ${change_bg_color}`}>
        <div className='main_button_box'>
          <button onClick={() => { change_button_state1() }}></button>
          <button onClick={() => { change_button_state2() }}></button>
          <button onClick={() => { change_button_state3() }}></button>
        </div>
        <div className='d-flex flex-column justify-content-center align-items-center'>
          <h1 className='text-light'>總</h1>
          <h3 className='text-light'>收支</h3>
        </div>
      </div>
      <div className='col-9 row p-5 d-flex flex-column border'>
        <div className='border text-center'>
          <h1 className={`${change_text_color}`}>${props.price_total}</h1>
        </div>
        <div className='border d-flex justify-content-center text-center '>
          <h5>開始日期:</h5>
          <input type="date" value={props.start_date} onChange={(e) => { props.set_start_date(e.target.value) }} className='mx-5' />
          <h5>結束日期:</h5>
          <input type="date" value={props.end_date} onChange={(e) => { props.set_end_date(e.target.value) }} className='mx-5' />
        </div>

      </div>
    </div>



  </div>
}

export default Total;
