import './Home.css';
import { useEffect, useState } from 'react';
import Head from './component/head/head';
import Main from './component/main/main';
import Total from './component/total/total';
import "bootstrap/dist/css/bootstrap.css"
import axios from 'axios';




function App() {
  const [test, setTest] = useState([]);

  function tt() {
    setTest([...test, 2]);
  }

  // console.log(test);


  // 一開始就取得input資料
  const [database_data, set_database_data] = useState([])
  //篩選過收支的資料
  const [select_mode, set_select_mode] = useState([])
  // console.log(select_mode)
  // 篩選過日期的資料
  const [select_date_data, set_select_date_data] = useState([])
  // console.log(select_date_data)
  // 輸入的日期
  const [start_date, set_start_date] = useState("")
  const [end_date, set_end_date] = useState("")
  //篩選過的資料的價格計算
  const [price_total, set_price_total] = useState([])


  useEffect(() => {
    const first_word = select_date_data.map(item => item.price.charAt(0));
    console.log(first_word)

    if (first_word.some(item => item.includes("+") || item.includes("-"))) {
      const total_plus = select_date_data
        .filter(item => item.charAt(0) == "+") // 選擇第一個字為 "+" 的項目
        .map(item => parseInt(item.slice(1))) // 去掉第一個字並轉換為數字
        .reduce((total, num) => total + num, 0); // 相加總和

      console.log(total_plus);

      const total_minus = select_date_data
        .filter(item => item.charAt(0) == "-") // 選擇第一個字為 "+" 的項目
        .map(item => parseInt(item.slice(1))) // 去掉第一個字並轉換為數字
        .reduce((total, num) => total + num, 0); // 相加總和

      console.log(total_minus);


      const total = total_plus - total_minus;
      console.log(total)
    }

    if (first_word.every(item => item.includes("+"))) {
      const plus_total_plus = select_date_data.reduce((total, item) => {
        return item.price.charAt(0) == "+" ? total + parseInt(item.price.slice(1)) : 0;
      }, 0);

      console.log(plus_total_plus);
    }

    if (first_word.every(item => item.includes("-"))) {
      const minus_total_minus = select_date_data.reduce((total, item) => {
        return item.price.charAt(0) == "-" ? total + parseInt(item.price.slice(1)) : 0;
      }, 0);

      console.log(minus_total_minus);
    }

  }, [select_date_data]);



  useEffect(() => {
    const get_origin_data = axios.get("http://localhost:8080/getData")
      .then(response => {
        set_database_data(response.data)
        set_select_mode(response.data)
      })
      .catch(error => {
        console.log(error)
      })
  }, [])

  // 更新後取得input資料

  const get_data_function = () => {
    const get_data = axios.get("http://localhost:8080/getData")
      .then(response => {
        set_database_data(response.data);
        set_select_mode(response.data)


      })
      .catch(error => {
        console.log(error)
      })
  }


  return (
    <div>
      <Head get_data_function={get_data_function} set_database_data={set_database_data} />
      <Total database_data={database_data} set_database_data={set_database_data} select_mode={select_mode} set_select_mode={set_select_mode} start_date={start_date} set_start_date={set_start_date} end_date={end_date} set_end_date={set_end_date} select_date_data={select_date_data} set_select_date_data={set_select_date_data} price_total={price_total} set_price_total={set_price_total} />
      {select_date_data.length === 0 ? (
        database_data.map(({ id, name, shop, price, today_date, note }) => (
          <Main
            key={id}
            id={id}
            name={name}
            shop={shop}
            price={price}
            today_date={today_date}
            note={note}
          />
        ))
      ) : (
        select_date_data.map(({ id, name, shop, price, today_date, note }) => (
          <Main
            key={id}
            id={id}
            name={name}
            shop={shop}
            price={price}
            today_date={today_date}
            note={note}
          />
        ))
      )}
    </div>
  );
}

export default App;
