import './Home.css';
import { useEffect, useState } from 'react';
import Head from './component/head/head';
import Main from './component/main/main';
import Total from './component/total/total';
import "bootstrap/dist/css/bootstrap.css"
import axios from 'axios';




function App() {

  // 一開始就取得input資料
  const [origin_database_data, set_origin_database_data] = useState([])

  useEffect(() => {
    const get_origin_data = axios.get("http://localhost:8080/getData")
      .then(response => {
        set_origin_database_data(response.data)
        set_database_data(response.data) 
        console.log(response.data)
      })
      .catch(error => {
        console.log(error)
      })
  }, [])

  // 更新後取得input資料
  const [database_data, set_database_data] = useState(origin_database_data)

  const get_data_function = () => {
    const get_data = axios.get("http://localhost:8080/getData")
      .then(response => {
        set_database_data(response.data)

      })
      .catch(error => {
        console.log(error)
      })
  }

  return (
    <div>
      <Head get_data_function={get_data_function} />
      <Total />
      {database_data.map(({ id, name, shop, price, today_date, note }) => (
        <Main
          key={id}
          id={id}
          name={name}
          shop={shop}
          price={price}
          today_date={today_date}
          note={note}
        />
      ))}
    </div>
  );
}

export default App;
