import React from 'react'
import { useEffect, useState } from 'react';


const Main = (props) => {
  const { id, name, shop, price, today_date, note } = props;

  //編輯的開關
  const [edit_switch, set_edit_switch] = useState(false)

  const name_switch = edit_switch ? <input type="text" placeholder='名稱' /> : <h1 className='ms-5'>{name}</h1>
  const shop_switch = edit_switch ? <input type="text" placeholder='店名' /> : <h3 className='ms-5'>{shop}</h3>
  const price_switch = edit_switch ? <input type="text" placeholder='價格' /> : <h1>${price}</h1>
  const today_date_switch = edit_switch ? <input type="date" placeholder='日期' /> : <h3>{today_date}</h3>
  const note_switch = edit_switch ? <input type="text" placeholder='備註' /> : <h3>{note}</h3>

  console.log(edit_switch)
  return (
    <div className='my-5'>
      <div className='row mx-5'>
        <div className='col-3 p-3  border'>
          {name_switch}
          {shop_switch}
        </div>
        <div className='col-9 row p-3 d-flex border'>
          <div className='col-4 border'>
            {price_switch}
          </div>
          <div className='col-6 border d-flex flex-column'>
            {today_date_switch}
            {note_switch}          
            </div>
          <div className='col-2 d-flex flex-column border'>
            <button className=' ' onClick={() => { set_edit_switch(true) }}>編輯</button>
            <button className=' ' onClick={() => {  }}>確定</button>
            <button className='mt-3 ' onClick={()=>{set_edit_switch(false)}}>取消</button>
          </div>
        </div>
      </div>



    </div>
  )
}

export default Main
