import './Home.css';
import { useEffect, useState } from 'react';
import Head from './component/head/head';
import Main from './component/main/main';
import Total from './component/total/total';
import "bootstrap/dist/css/bootstrap.css"
import axios from 'axios';




function App() {

  // 一開始就取得input資料
  const [origin_database_data, set_origin_database_data] = useState({})

  useEffect(() => {
    const get_origin_data = axios.get("http://localhost:8080/getData")
      .then(response => {
        set_origin_database_data(response.data)
        console.log(response.data)
      })
      .catch(error => {
        console.log(error)
      })
  }, [])

  // 更新後取得input資料
  const [database_data, set_database_data] = useState(origin_database_data)
  const get_data = axios.get("http://localhost:8080/getData")
    .then(response => {
      set_database_data(response.data)

    })
    .catch(error => {
      console.log(error)
    })

  const cat = () => { alert("嗚呼") }

  useEffect(() => {
    console.log(database_data);
  }, []);


  return (
    <div>
      <Head get_data={get_data} cat={cat} />
      <Total />
      <Main />


    </div>
  );
}

export default App;
