  }, [select_date_data])

  return (
    <div>
      <Head get_data_function={get_data_function} set_database_data={set_database_data} />
      <Total database_data={database_data} set_database_data={set_database_data} select_mode={select_mode} set_select_mode={set_select_mode} select_date_data={select_date_data} set_select_date_data={set_select_date_data} set_price_total={set_price_total} />
      {select_date_data.length === 0 ? (
        database_data.map(({ id, name, shop, price, today_date, note }) => (
          <Main
            key={id}
            id={id}
            name={name}
            shop={shop}
            price={price}
            today_date={today_date}
            note={note}
          />
        ))
      ) : (
        select_date_data.map(({ id, name, shop, price, today_date, note }) => (
          <Main
            key={id}
            id={id}
            name={name}
            shop={shop}
            price={price}
            today_date={today_date}
            note={note}
          />
